package com.example.controller;import com.example.dto.ApiResponse;import com.example.dto.AuthDTO;import com.example.dto.RegistrationDTO;import com.example.service.AuthService;import jakarta.servlet.Registration;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.http.ResponseEntity;import org.springframework.web.bind.annotation.PostMapping;import org.springframework.web.bind.annotation.RequestBody;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RestController;@RestController@RequestMapping("/api/v1/auth")public class AuthController {    @Autowired    private AuthService authService;    @PostMapping("/login")    public ResponseEntity<ApiResponse> login(@RequestBody AuthDTO dto) {        return ResponseEntity.ok(authService.login(dto));    }    @PostMapping("/registration")    public ResponseEntity<ApiResponse> registration(@RequestBody RegistrationDTO dto) {        return ResponseEntity.ok(authService.registration(dto));    }}