package com.example.controller;import com.example.dto.ApiResponse;import com.example.dto.PlaylistVideoDTO;import com.example.enums.Language;import com.example.service.PlaylistVideoService;import jakarta.validation.Valid;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.http.HttpStatus;import org.springframework.http.ResponseEntity;import org.springframework.security.access.prepost.PreAuthorize;import org.springframework.web.bind.annotation.*;@RestController@RequestMapping("/api/v1/playlist-video")public class PlaylistVideoController {    @Autowired    private PlaylistVideoService playlistVideoService;    @PreAuthorize("hasAnyRole('USER', 'ADMIN')")    @PostMapping("/close/create")    public ResponseEntity<ApiResponse> create(@Valid @RequestBody PlaylistVideoDTO dto) {        ApiResponse response = playlistVideoService.create(dto);        if (response.getStatus()) {            return ResponseEntity                    .status(HttpStatus.CREATED).body(response);        } else {            return ResponseEntity.badRequest().body(response);        }    }    @PreAuthorize("hasAnyRole('USER', 'ADMIN')")    @PutMapping("/close/update/{id}")    public ResponseEntity<ApiResponse> update(@PathVariable Integer id,                                              @Valid @RequestBody PlaylistVideoDTO dto,                                              @RequestParam(defaultValue = "en") Language lang) {        ApiResponse response = playlistVideoService.update(id, dto, lang);        if (response.getStatus()) {            return ResponseEntity                    .ok(response);        } else {            return ResponseEntity.badRequest().body(response);        }    }}