package com.example.controller;import com.example.dto.ApiResponse;import com.example.dto.PlaylistVideoDTO;import com.example.service.PlaylistVideoService;import jakarta.validation.Valid;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.http.HttpStatus;import org.springframework.http.ResponseEntity;import org.springframework.security.access.prepost.PreAuthorize;import org.springframework.web.bind.annotation.PostMapping;import org.springframework.web.bind.annotation.RequestBody;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RestController;@RestController@RequestMapping("/api/v1/playlist-video")public class PlaylistVideoController {    @Autowired    private PlaylistVideoService playlistVideoService;    @PreAuthorize("hasAnyRole('USER', 'ADMIN')")    @PostMapping("/close/create")    public ResponseEntity<ApiResponse> create(@Valid @RequestBody PlaylistVideoDTO dto) {        ApiResponse response = playlistVideoService.create(dto);        if (response.getStatus()) {            return ResponseEntity                    .status(HttpStatus.CREATED).body(response);        } else {            return ResponseEntity.badRequest().body(response);        }    }}