package com.example.controller;import com.example.dto.ApiResponse;import com.example.dto.AttachDTO;import com.example.dto.ProfileDTO;import com.example.service.EmailSenderService;import com.example.service.ProfileService;import com.example.util.HTMLUtil;import jakarta.servlet.http.HttpServletResponse;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.http.HttpStatus;import org.springframework.http.ResponseEntity;import org.springframework.security.access.prepost.PostAuthorize;import org.springframework.web.bind.annotation.*;import java.io.IOException;@RestController@RequestMapping("/api/v1/profile")public class ProfileController {    @Autowired    private ProfileService profileService;    @Autowired    private EmailSenderService emailSenderService;    @PostAuthorize("hasAnyRole('ADMIN')")    @PostMapping("/admin/create")    public ResponseEntity<?> create(@RequestBody ProfileDTO dto) {        return ResponseEntity.ok(profileService.create(dto));    }    @PutMapping(value = "/open/update/detail/{profileId}")    public ResponseEntity<?> updateDetail(@PathVariable Integer profileId,                                          @RequestBody ProfileDTO dto) {        return ResponseEntity.ok(profileService.updateDetail(profileId, dto));    }    @PutMapping(value = "/open/update/email/{email}")    public ResponseEntity<?> updateEmail(@PathVariable String email) {        return ResponseEntity.ok(profileService.updateEmail(email));    }    @GetMapping("/open/get/{profileId}")    public ResponseEntity<?> getDetail(@PathVariable Integer profileId) {        return ResponseEntity.ok(profileService.getDetail(profileId));    }    @PutMapping("/open/update-photo/{profileId}")    public ResponseEntity<?> updateAttach(@PathVariable Integer profileId,                                          @RequestBody AttachDTO dto) {        return ResponseEntity.ok(profileService.updateAttach(profileId, dto));    }}