package com.example.controller;import com.example.dto.JwtDTO;import com.example.dto.ProfileDTO;import com.example.service.ProfileService;import com.example.util.SecurityUtil;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.http.ResponseEntity;import org.springframework.web.bind.annotation.*;@RestController@RequestMapping("/api/v1/profile")public class ProfileController {    @Autowired    private ProfileService profileService;    @PostMapping(value = {"", "/"})    public ResponseEntity<?> create(@RequestBody ProfileDTO dto){        return ResponseEntity.ok(profileService.create(dto));    }    @PutMapping(value = "/{id}")    public ResponseEntity<?> updateDetail(@RequestBody ProfileDTO dto,                                          @RequestHeader("Authorization") String authToken){        JwtDTO jwtDTO = SecurityUtil.hasRole(authToken, null);        return ResponseEntity.ok(profileService.updateDetail(jwtDTO.getId(), dto));    }}