package com.example.controller;import com.example.dto.ProfileDTO;import com.example.service.ProfileService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.http.ResponseEntity;import org.springframework.web.bind.annotation.PostMapping;import org.springframework.web.bind.annotation.RequestBody;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RestController;@RestController@RequestMapping("/api/v1/profile")public class ProfileController {    @Autowired    private ProfileService profileService;    @PostMapping(value = {"", "/"})    public ResponseEntity<?> create (@RequestBody ProfileDTO dto){        return ResponseEntity.ok(profileService.create(dto));    }}