package com.example.repository;import com.example.entity.CommentEntity;import org.springframework.data.domain.Page;import org.springframework.data.domain.Pageable;import org.springframework.data.jpa.repository.JpaRepository;import org.springframework.data.jpa.repository.Query;import org.springframework.data.repository.query.Param;import java.time.LocalDateTime;public interface CommentRepository extends JpaRepository<CommentEntity, String> {    Page<CommentEntity> findAllByProfileId(Integer id, Pageable pageable);    Page<CommentEntity> findAllByContent(String content, Pageable pageable);    Page<CommentEntity> findAllByCreatedDateBetween(LocalDateTime beginning, LocalDateTime ending, Pageable pageable);    Page<CommentEntity> findAllByLikeCount(Long count, Pageable pageable);    Page<CommentEntity> findAllByDislikeCount(Long count, Pageable pageable);    Page<CommentEntity> findAllByVideoId(String id, Pageable pageable);    @Query(value = "select c" +            " from comment c" +            " inner join video v" +            " on c.video_id = v.id" +            " where v.title like concat('%',:title,'%')", nativeQuery = true)    Page<CommentEntity> findAllByVideoTitle(@Param("title") String title, Pageable pageable);}