package com.example.repository;import com.example.entity.CommentEntity;import org.springframework.data.domain.Page;import org.springframework.data.domain.Pageable;import org.springframework.data.jpa.repository.JpaRepository;import java.time.LocalDateTime;public interface CommentRepository extends JpaRepository<CommentEntity, String> {    Page<CommentEntity> findAllByProfileId(Integer id, Pageable pageable);    Page<CommentEntity> findAllByContent(String content, Pageable pageable);    Page<CommentEntity> findAllByCreatedDateBetween(LocalDateTime beginning, LocalDateTime ending, Pageable pageable);    Page<CommentEntity> findAllByLikeCount(Long count, Pageable pageable);    Page<CommentEntity> findAllByDislikeCount(Long count, Pageable pageable);}