package com.example.repository;import com.example.entity.PlaylistVideoEntity;import com.example.entity.VideoEntity;import org.springframework.data.domain.Page;import org.springframework.data.jpa.repository.JpaRepository;import org.springframework.data.jpa.repository.Query;import org.springframework.data.repository.query.Param;import org.springframework.transaction.annotation.Transactional;import java.util.List;import java.util.Optional;public interface PlaylistVideoRepository extends JpaRepository<PlaylistVideoEntity, Integer> {    @Transactional    void deleteByPlaylistId(Integer id);    @Transactional    void deleteByVideoId(String id);    Optional<PlaylistVideoEntity> findByVideoId(String id);    Optional<PlaylistVideoEntity> findByPlaylistId(Integer id);    @Query(value = "select v" +            " from playlist_video pv" +            " inner join video v" +            " on pv.video_id = v.id" +            " where pv.playlist_id = :playlistId", nativeQuery = true)    List<VideoEntity> getVideoListByPlaylistId(@Param("playlistId") Integer playlistId);}