package com.example.repository;import org.springframework.data.jpa.repository.JpaRepository;import com.example.entity.ProfileEntity;import org.springframework.data.jpa.repository.Modifying;import org.springframework.data.jpa.repository.Query;import org.springframework.transaction.annotation.Transactional;import java.util.Optional;public interface ProfileRepository extends JpaRepository<ProfileEntity, Integer> {    Optional<ProfileEntity> findByEmail(String email);    @Transactional    @Modifying    @Query("update ProfileEntity  set name =?2, surname =?3 where id =?1")    int updateDetail(Integer id, String name, String surname);}