package com.example.service;import com.example.config.CustomUserDetails;import com.example.dto.ApiResponse;import com.example.dto.AttachDTO;import com.example.dto.ProfileDTO;import com.example.entity.ProfileEntity;import com.example.enums.ProfileRole;import com.example.enums.ProfileStatus;import com.example.exception.AppBadRequestException;import com.example.repository.ProfileRepository;import com.example.util.MD5Util;import com.example.util.SpringSecurityUtil;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import java.util.Optional;@Servicepublic class ProfileService {    @Autowired    private ProfileRepository profileRepository;    @Autowired    private EmailSenderService emailSenderService;    public ProfileDTO create(ProfileDTO dto) {        CustomUserDetails customUserDetails = SpringSecurityUtil.getCurrentUser();        Optional<ProfileEntity> profileByEmile = profileRepository.findByEmail(dto.getEmail());        if (profileByEmile.isPresent()) {            throw new AppBadRequestException("Email already exists");        }        ProfileEntity entity = new ProfileEntity();        entity.setName(dto.getName());        entity.setSurname(dto.getSurname());        entity.setEmail(dto.getEmail());        entity.setPassword(MD5Util.encode(dto.getPassword()));        entity.setStatus(ProfileStatus.ACTIVE);        entity.setRole(ProfileRole.ROLE_ADMIN);        entity.setPrtId(customUserDetails.getProfile().getId());        profileRepository.save(entity);        dto.setId(entity.getId());        dto.setCreatedDate(entity.getCreatedDate());        return dto;    }    public Boolean updateDetail(Integer profileId,ProfileDTO dto) {        int effectedRows = profileRepository.updateDetail(profileId, dto.getName(), dto.getSurname());        return effectedRows == 1;    }    public ProfileDTO getDetail(Integer profileId) {        ProfileEntity entity = profileRepository.getDetail(profileId);        ProfileDTO dto = new ProfileDTO();        dto.setId(entity.getId());        dto.setName(entity.getName());        dto.setSurname(entity.getSurname());        dto.setEmail(entity.getEmail());        dto.setPhotoUrl(entity.getPhotoUrl());        return dto;    }    public Boolean updateAttach(Integer profileId, AttachDTO attachDTO) {        int effectedRows = profileRepository.updateAttach(profileId, attachDTO.getId());        return effectedRows == 1;    }    public ApiResponse updateEmail(String email) {        CustomUserDetails customUserDetails = SpringSecurityUtil.getCurrentUser();        ProfileEntity profile = customUserDetails.getProfile();        ApiResponse response = emailSenderService                .sendUpdateEmailVerification(profile.getEmail(), email);        return response;    }    public Boolean changePassword(String newPassword){        CustomUserDetails customUserDetails = SpringSecurityUtil.getCurrentUser();        ProfileEntity entity = customUserDetails.getProfile();        entity.setPassword(newPassword);        profileRepository.save(entity);        return true;    }}