--INCREASE VIDEO COUNT FUNCTION & TRIGGERcreate or replace function increase_video_views()    returns trigger as$$BEGIN    update video    set view_count = view_count + 1    where id = :id;END;$$ language plpgsql;create trigger increase_video_views_trigger    after insert    on video_watched    for each rowexecute function increase_video_views();-- CREATE VIDEOINSERT INTO public.video (category_id, visible, created_date, dislike_count, like_count, published_date, shared_count, view_count, title, attach_id,                          channel_id, description, id, preview_attach_id, video_status, video_type)VALUES (1, true, '2023-08-14 16:41:33.000000', 0, 0, '2023-08-14 16:41:41.000000', 0, 0, 'SALOM', '1', '1', 'salom berish odobi', '1', '1', 'PUBLIC',        'SHORT');--PAGING ONLY ADMIN BY MAPPERselect v.id as video_id,       v.title,       v.category_id,       v.attach_id,       v.channel_id,       v.description,       v.video_type,       v.video_status,       v.created_date,       p.id as prt_id,       p.name,       p.surnamefrom video as v         inner join profile as p                    on v.prt_id = p.id;